// Helper: map ASCII letter to index in the table; returns -1 if not A-Z/a-z.
static inline int font16x16_letter_index(char c) {
    if (c >= 'A' && c <= 'Z') return (int)(c - 'A');
    if (c >= 'a' && c <= 'z') return 26 + (int)(c - 'a');
    return -1;
}
